import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Recurses() {
    const userId = useState(Number(localStorage.getItem('userId')))[0];
    const ponto = useState(Number(localStorage.getItem('ponto')))[0];
    const cadastro = useState(Number(localStorage.getItem('cadastro')))[0];
    const relatorio = useState(Number(localStorage.getItem('relatorio')))[0];
    const indicadores = useState(Number(localStorage.getItem('indicadores')))[0];

    return (
        <div>
            <h2>Bem-vindo à página de recursos</h2>
            <p>User ID: {userId}</p>
            <ul>
                {ponto === 1 && 
                    <li>
                        <Link to="/Registropt">
                            <button className="btn-app">Registrar Ponto</button>
                        </Link>
                    </li>
                }
                {cadastro === 1 && 
                    <li>
                        <Link to="/Cadastro">
                            <button className="btn-app">Cadastro</button>
                        </Link>
                    </li>
                }
                {relatorio === 1 && 
                    <li>
                        <Link to="/Relatorios">
                            <button className="btn-app">Relatórios</button>
                        </Link>
                    </li>
                }
                {indicadores === 1 && 
                    <li>
                        <Link to="/Indicadores">
                            <button className="btn-app">Indicadores</button>
                        </Link>
                    </li>
                }
            </ul>
        </div>
    );
}

export default Recurses;
