import React, { useState, useEffect } from 'react';

// Função para obter a data atual formatada
const getDataAtual = () => {
    const hoje = new Date();
    return hoje.toLocaleDateString();
};

// Função para obter a hora atual formatada
const getHoraAtual = () => {
    const agora = new Date();
    return agora.toLocaleTimeString();
};

function RegistroPonto() {
    const data = getDataAtual();
    const [hora, setHora] = useState(getHoraAtual());

    useEffect(() => {
        const intervalo = setInterval(() => {
            setHora(getHoraAtual());
        }, 1000);
        
        // Limpar intervalo ao desmontar o componente
        return () => clearInterval(intervalo);
    }, []);

    return (
        <div>
            <h2>Registro de ponto</h2>
            <h3>Pontos do dia {data}</h3>
            <h3>Hora: {hora}</h3>
        </div>
    );
}

export default RegistroPonto;
