import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Recurses() {
    const userId = useState(Number(localStorage.getItem('userId')));
    const ponto = useState(Number(localStorage.getItem('Ponto')));
    const cadastro = useState(Number(localStorage.getItem('cadastro')));
    const relatorios = useState(Number(localStorage.getItem('relatorios')));
    const indicadores = useState(Number(localStorage.getItem('indicadores')));

    return (
        <div>
            <h2>Bem-vindo à página de recursos</h2>
            <p>User ID: {userId}</p>
            <ul>
                {ponto === 1 && 
                    <Link to="/Registropt">
                        <button className="btn-app">Registrar Ponto</button>
                    </Link>
                }
            </ul>
        </div>
    );
}

export default Recurses;
