import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import md5 from 'md5';

function Login() {
    const [cpf, setCPF] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const history = useHistory();

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        const hashedPassword = md5(password);

        try {
            const response = await axios.post('http://localhost:3001/login', {
                cpf,
                password: hashedPassword
            });

            console.log('Login realizado com sucesso');

            // Extraindo o ID do usuário da resposta
            const { id } = response.data;

            // Salvando o ID do usuário no localStorage
            localStorage.setItem('userId', id);

            // Redirecionando para a página de recursos após o login bem-sucedido
            history.push('/recursos');

            // Você pode adicionar lógica adicional aqui após o login

        } catch (error) {
            console.error('Erro ao fazer login:', error);
            setError('Erro ao fazer login. Verifique suas credenciais.');
        } finally {
            setLoading(false);
        }
    };

    const handleCPFChange = (e) => {
        const formattedCPF = formatCPF(e.target.value);
        setCPF(formattedCPF);
    };

    return (
        <div className='login-form-wrap'>
            <h2>Login</h2>
            <form className='login-form' onSubmit={handleLogin}>
                <input
                    type='text'
                    name='cpf'
                    placeholder='CPF'
                    required
                    value={cpf}
                    onChange={handleCPFChange}
                />
                <input
                    type='password'
                    name='senha'
                    placeholder='Senha'
                    required
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type='submit' className='btn-login' disabled={loading}>
                    {loading ? 'Carregando...' : 'Login'}
                </button>
                {error && <p className='error'>{error}</p>}
            </form>
        </div>
    );
}

export default Login;
