import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Recurses() {
    const [userId, setUserId] = useState(Number(localStorage.getItem('userId')));
    const [reloadPage, setReloadPage] = useState(true);

    useEffect(() => {
        if (reloadPage) {
            // Forçar o recarregamento da página ao entrar na rota '/recurses'
            setReloadPage(false); // Marcar que o recarregamento já foi feito
            window.location.reload();
        }
    }, [reloadPage]);

    return (
        <div>
            <h2>Bem-vindo à página de recursos</h2>
            <p>User ID: {userId}</p>
            <ul>
                {userId === 1 && 
                    <Link to="/Registropt">
                        <button className="btn-app">Registrar Ponto</button>
                    </Link>
                }
            </ul>
        </div>
    );
}

export default Recurses;
