import React, { useState } from 'react';
import axios from 'axios';
import md5 from 'md5';
import { useHistory } from 'react-router-dom'; // Importando o useHistory

function Login() {
    const [cpf, setCPF] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const history = useHistory(); // Instanciando o useHistory

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        const hashedPassword = md5(password);

        try {
            const response = await axios.post('http://localhost:3001/login', {
                cpf,
                password: hashedPassword
            });

            console.log('Login realizado com sucesso:', response.data);

            // Extrair o ID do usuário da resposta
            const userId = response.data.userId;

            // Armazenar o ID do usuário localmente (por exemplo, usando localStorage)
            localStorage.setItem('userId', userId);

            // Redirecionar para outra rota após o login bem-sucedido
            history.push('/recurses'); // Substitua '/recursos' pela rota desejada

        } catch (error) {
            console.error('Erro ao fazer login:', error);
            setError('Erro ao fazer login. Verifique suas credenciais.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='login-form-wrap'>
            <h2>Login</h2>
            <form className='login-form' onSubmit={handleLogin}>
                <input
                    type='text'
                    name='cpf'
                    placeholder='CPF'
                    required
                    value={cpf}
                    onChange={(e) => setCPF(e.target.value)}
                    aria-label='CPF'
                />
                <input
                    type='password'
                    name='senha'
                    placeholder='Senha'
                    required
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    aria-label='Senha'
                />
                <button type='submit' className='btn-login' disabled={loading}>
                    {loading ? 'Carregando...' : 'Login'}
                </button>
                {error && <p className='error'>{error}</p>}
            </form>
        </div>
    );
}

export default Login;
