import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Assegure-se de ter react-router-dom instalado

// Função para obter a data atual formatada
const getDataAtual = () => {
    const hoje = new Date();
    return hoje.toLocaleDateString();
};

// Função para obter a hora atual formatada
const getHoraAtual = () => {
    const agora = new Date();
    return agora.toLocaleTimeString();
};

function RegistroPonto() {
    const data = getDataAtual();
    const [hora, setHora] = useState(getHoraAtual());
    const [userId, setUserId] = useState(null);
    const [nome, setNome] = useState('');
    const [registros, setRegistros] = useState([]);
    const [tpPonto, setTpPonto] = useState(false); // Inicializa tp_ponto como false
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate(); // Hook para navegação

    useEffect(() => {
        const userIdFromStorage = Number(localStorage.getItem('userId'));
        const nomeFromStorage = localStorage.getItem('nome');
        setUserId(userIdFromStorage);
        setNome(nomeFromStorage);

        const intervalo = setInterval(() => {
            setHora(getHoraAtual());
        }, 1000);

        // Limpar intervalo ao desmontar o componente
        return () => clearInterval(intervalo);
    }, []);

    const registrarPonto = async () => {
        const tipoPonto = tpPonto ? 'saída' : 'entrada';
        const novoRegistro = { data: getDataAtual(), hora: getHoraAtual(), tipo: tipoPonto };
        setRegistros([...registros, novoRegistro]);
        setTpPonto(!tpPonto);
        setLoading(true);

        try {
            const response = await axios.post('http://localhost:3001/auth/ponto', {
              userId,
              data: getDataAtual(),
              hora: getHoraAtual(),
            });
      
            const { id, nome, perfil, ponto, cadastro, relatorio, indicadores } = response.data;
            localStorage.setItem('userId', id);
            localStorage.setItem('nome', nome);
            localStorage.setItem('perfil', perfil);
            localStorage.setItem('ponto', ponto);
            localStorage.setItem('cadastro', cadastro);
            localStorage.setItem('relatorio', relatorio);
            localStorage.setItem('indicadores', indicadores);
      
            navigate('/recurses');
      
        } catch (error) {
            setError('Erro ao registrar ponto. Verifique suas credenciais.');
        } finally {
            setLoading(false);
        }
    };

    const descricaoBotao = tpPonto ? 'Saída' : 'Entrada';

    return (
        <div>
            <h2>Registro de ponto</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <p>Nome: {nome}</p>
            <p>User ID: {userId}</p>
            <h3>Pontos do dia {data}</h3>
            <button className={`btn-app-${descricaoBotao.toLowerCase()}`} onClick={registrarPonto} disabled={loading}>
                {loading ? 'Registrando...' : `Registrar ${descricaoBotao} às ${hora}`}
            </button>
            <h3>Registros:</h3>
            <ul>
                {registros.map((registro, index) => (
                    <li key={index}>
                        {registro.data} - {registro.hora} ({registro.tipo})
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default RegistroPonto;
